parameters.jsCode:
const items = $input.all();

for (const item of items) {
  const utcDate = new Date(item.json.snippet.topLevelComment.snippet.publishedAt);

  const localTime = new Intl.DateTimeFormat('en-GB', {
    timeZone: 'Asia/Kolkata',
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false,
  }).format(utcDate);

  item.json.local_time = localTime.replace(',', '');
}

return items;

